import Document, { Html, Head, Main, NextScript } from 'next/document'
import { ServerStyleSheet } from 'styled-components'
import { IS_PRODUCTION } from '~/constants/constants'
import theme from '~/theme'
import * as Sentry from '@sentry/browser'
import { GA_TRACKING_ID } from '~/lib/gtag'

process.on('unhandledRejection', (err) => {
    Sentry.captureException(err);
});

process.on('uncaughtException', (err) => {
    Sentry.captureException(err);
});

export default class MyDocument extends Document {

  static async getInitialProps(ctx) {
    const sheet = new ServerStyleSheet()
    const originalRenderPage = ctx.renderPage

    try {
      ctx.renderPage = () =>
        originalRenderPage({
          enhanceApp: App => props => sheet.collectStyles(<App {...props} />),
        })

      const initialProps = await Document.getInitialProps(ctx)
      return {
        ...initialProps,
        styles: (
          <>
            {initialProps.styles}
            {sheet.getStyleElement()}
          </>
        ),
      }
      // return {
      //   ...initialProps,
      //   // Styles fragment is rendered after the app and page rendering finish.
      //   styles: [...React.Children.toArray(initialProps.styles), sheets.getStyleElement()],
      // };
    } finally {
      sheet.seal()
    }
  }

  render() {
    return (
      <Html>
        <Head>

          <meta charSet="utf-8" />
          <meta name="viewport" content="initial-scale=1, shrink-to-fit=yes, width=device-width" />

          {/* PWA primary color */}
          <meta name="theme-color" content={theme.colors.primary} />

          {/* Global Site Tag (gtag.js) - Google Analytics */}
          <script
            async
            src={`https://www.googletagmanager.com/gtag/js?id=${GA_TRACKING_ID}`}
          />
          <script
            dangerouslySetInnerHTML={{
              __html: `
            window.dataLayer = window.dataLayer || [];
            function gtag(){dataLayer.push(arguments);}
            gtag('js', new Date());
            gtag('config', '${GA_TRACKING_ID}', {
              page_path: window.location.pathname,
            });
          `,
            }}
          />

          {/* styles */}
          { IS_PRODUCTION ? <>
            {/* bootstrap */}
            <link
              crossOrigin="anonymous"
              href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
              integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
              rel="stylesheet"
              />
            {/* slick */}
            <link
              charSet="utf-8"
              href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick.min.css"
              rel="stylesheet"
              type="text/css"
            />
            <link
              href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick-theme.min.css"
              rel="stylesheet"
              type="text/css"
            />
            {/* roboto & roboto condensed */}
            <link
              href="https://fonts.googleapis.com/css?family=Roboto:400,700|Roboto+Condensed:700&amp;display=swap"
              rel="stylesheet"
            />
          </> : <>
            {/* bootstrap */}
            <link rel="stylesheet" href="/static/css/bootstrap.min.css" />
            {/* slick */}
            <link rel="stylesheet" href="/static/css/slick.css" />
            <link rel="stylesheet" href="/static/css/slick-theme.css" />
            {/* roboto & roboto condensed */}
            <link rel="stylesheet" href="/static/fonts/roboto/stylesheet.css" />
          </> }

          {/* bebas */}
          <link rel="stylesheet" href="/static/fonts/bebas-neue/stylesheet.css" />

        </Head>
        <body>
          <Main />
          <NextScript />
        </body>
      </Html>
    )
  }
}
