import { useContext, useEffect, useState } from "react";
import FormGroup from "~/components/layout/AuthModal/FormGroup";
import Label from "~/components/Form/Label";
import Input from "~/components/layout/AuthModal/Input";
import InputRadio from "~/components/Form/InputRadio";
import withAuth from "~/components/withAuth";
import InvalidFeedback from "~/components/Form/InvalidFeedback"
import { HAS_WINDOW } from "~/constants/constants";
import useScript from "@charlietango/use-script";
import Select from "~/components/Select/Select";
import Button from "~/components/button";
import MaskedInput from 'react-text-mask'
import UserContext from "~/contexts/UserContext";

const Payment = ({
                   layoutProps,
                   userData,
                   package_id,
                   packages,
                   api,
                   error,
                   requireds
                 }) => {

  const [ready, status] = useScript('https://secure.mlstatic.com/sdk/javascript/v1/mercadopago.js')


  let selectedPackage = null
  if (packages && package_id) {
    selectedPackage = packages.items.find(i => i.id == package_id)
    console.log(selectedPackage)
  }

  const { user } = useContext(UserContext)


  const [values, setValues] = useState({
    package_id: '',
    payment_method_id: null,
    payment_os: null,
    cash_payment_method_id: null,
    terms: null,
    discount_id: null,
    cardNumber: null,
    cardHolderName: null,
    cardExpirationDate: null,
    cardSecurityCode: null
  })

  const [cardImg, setCardImg] = useState(null);
  const [creditCard, setCreditCard] = useState(null);

  const [loading, setLoading] = useState();

  const businessUnitPublicKey = 'TEST-7205c1be-9805-4308-bf00-6557c5b56b88'
  const [isMercadoPagoReady, setIsMercadoPagoReady] = useState(false)
  const MercadoPago = ready && HAS_WINDOW ? window.Mercadopago : null

  const credit_card_id = 1
  const debit_card_id = 2
  const isCardPayment = [credit_card_id, debit_card_id].includes(values.payment_method_id)


  useEffect(_ => {
    if (MercadoPago) {
      MercadoPago.setPublishableKey(businessUnitPublicKey)
      setIsMercadoPagoReady(true)
    }
  }, [MercadoPago])


  /* handle payment method change */
  function onPaymentChange(e) {
    setValues({
      ...values,
      payment_method_id: parseInt(e.target.value, 10),
      cash_payment_method_id: null,
    })
  }

  function onCashPaymentMethodChange(e) {
    setValues({
      ...values,
      cash_payment_method_id: parseInt(e.target.value, 10),
    })
  }

  const handleInputChange = e => {
    const { checked, name, value, type } = e.target
    setValues({
      ...values,
      [name]: type === 'checkbox' ?
        (checked ? (value === 'true' ? true : value) : false) :
        value,
    })
  }

  const handleInputChangeCreditCardNumber = e => {

    const { name, value } = e.target

    setValues({
      ...values,
      [name]: value,
    })

    let cardnumber = value;

    if (cardnumber.length >= 6) {
      let bin = cardnumber.substring(0, 6);

      MercadoPago.getPaymentMethod({
        "bin": bin
      }, (status, details) => {
        setCardImg(details[0].secure_thumbnail);
        setCreditCard(details[0]);
      });

    }
  }

  // payment methods
  const [paymentMethods, setPaymentMethods] = useState()

  // get payment methods
  useEffect(_ => {
    const getPaymentMethods = async _ => {
      const { data } = await api.get('payment-methods')
      setPaymentMethods(data)
    }
    getPaymentMethods()
  }, [])

  // cash payment methods
  const [cashPaymentMethods, setCashPaymentMethods] = useState()

  // get cash payment methods
  useEffect(_ => {
    const getCashPaymentMethods = async _ => {
      const { data } = await api.get('cash-payment-methods')
      setCashPaymentMethods(data)
    }
    getCashPaymentMethods()
  }, [])

  /* token */
  async function createToken() {

    const promise = new Promise((resolve, reject) => {

      const additionalData = {
        holder_name: document.getElementById('cardholder-name').value,
        // custom_data: document.getElementById('custom').value,
      }

      MercadoPago.createToken(additionalData, (result) => {
        const json = JSON.parse(result)
        console.log('json', json)
        json.token ? resolve(json) : reject(json)
      })

    })

    return promise

  }


  const submitCredit = async e => {

    e.preventDefault();

    // console.log(MercadoPago.getIdentificationTypes());
    // return;
    setLoading(true);

    const paymentData = values.package_id && values.payment_method_id && isCardPayment ? await createToken() : null

    let expirationMonth = values.cardExpirationDate.split('/')[0];
    let expirationYear = values.cardExpirationDate.split('/')[1];




    console.log({
      cardNumber: values.cardNumber,
      cardholderName: values.cardHolderName,
      cardExpirationMonth: expirationMonth,
      cardExpirationYear: expirationYear,
      securityCode: values.cardSecurityCode,
      docType: "",
      docNumber: '',
      email: user.email
    })

    MercadoPago.createToken({
      cardNumber: values.cardNumber,
      cardholderName: values.cardHolderName,
      cardExpirationMonth: expirationMonth,
      cardExpirationYear: expirationYear,
      securityCode: values.cardSecurityCode,
      docType: "",
      docNumber: '',
      email: user.email
    }, (a, b) => {

      console.log(a)
      console.log(b)
    });


    // const data = { ...values, payment_os: paymentData }
    //
    // try {
    //
    //   const res = await api.post(`register/subscribe`, {
    //     package_id: package_id,
    //     payment_method_id: values.payment_method_id,
    //     token: ""
    //   })
    //
    //   // handleSubmit(1)
    //
    // } catch (error) {
    //
    //   if (error.response) {
    //     const { data, status } = error.response
    //     if (status === 422) {
    //       setError(data)
    //     }
    //   } else if (error.request) {
    //     setError(error)
    //   } else {
    //     setError(error)
    //   }
    //
    // } finally {
    //   setLoading(false)
    // }


  }

  return (
    <div className="row">
      <div className="offset-md-2 col-md-8">

        <h3 className="h3">Pago</h3>

        <div className="row">

          <div className="col-md-6 paymentMethod">
            <div className="row">
              <div className="col-12">
                <input type="text" id={"docType"}/>
                <FormGroup>
                  <Label htmlFor="payment_method_id" style="margin: 0;">Pague com</Label>
                  <Select
                    id="payment_method_id"
                    name="payment_method_id"
                    required={ requireds }
                    onChange={ onPaymentChange }
                    value={ values.payment_method_id }
                  >
                    <option value="0">Selecione</option>
                    { paymentMethods && paymentMethods.map((paymentMethod, key) => (
                      <option
                        key={ paymentMethod.id }
                        state={ values.payment_method_id }
                        value={ paymentMethod.id }
                      >
                        { paymentMethod.name }
                      </option>
                    )) }
                  </Select>
                  <InvalidFeedback error={ error } loading={ loading } name="gender_id"/>
                </FormGroup>
              </div>
              <div className="col-12">
                <FormGroup>
                  <Label htmlFor="cardHolderName">Nombre impreso en tarjeta</Label>
                  <Input id="cardHolderName" name="cardHolderName" required={ requireds } type="text"
                         onChange={ handleInputChange }/>
                </FormGroup>
              </div>
              <div className="col-12">
                <FormGroup>
                  <Label htmlFor="card-number">Nro Cartão</Label>
                  <img src={ cardImg } class="creditCardBrand"/>
                  <MaskedInput
                    mask={[/\d/, /\d/, /\d/, /\d/, ' ', /\d/, /\d/, /\d/, /\d/, ' ', /\d/, /\d/, /\d/, /\d/, ' ', /\d/, /\d/, /\d/, /\d/]}
                    guide={false}
                    class={"form-control"}
                    // placeholder={"***"}
                    onChange={ handleInputChangeCreditCardNumber }
                    id="cardNumber" name="cardNumber"
                  />
                  {/*<Input id="card-number" name="card-number" value={ values.credit_card_number } required={ requireds }*/}
                  {/*       type="text"  maxLength={ 19 }/>*/}
                </FormGroup>
              </div>
              <div className="col-6">
                <FormGroup>
                  <Label htmlFor="card-duedate">Dt Vencimento</Label>
                  {/*<InputMask mask="99/99" class="form-control" value={values.due_date} onChange={handleInputChange} maskChar={null} placeholder={"MM/YY"}/>*/}
                  <MaskedInput
                    mask={[/\d/, /\d/, '/', /\d/, /\d/,]}
                    guide={false}
                    class={"form-control"}
                    placeholder={"MM/AA"}
                    name={"cardExpirationDate"}
                    onChange={handleInputChange}
                  />
                </FormGroup>
              </div>
              <div className="col-6">
                <FormGroup>
                  <Label htmlFor="card-cvv">CVV</Label>
                  <MaskedInput
                    mask={[/\d/, /\d/, /\d/]}
                    guide={false}
                    class={"form-control"}
                    name={"cardSecurityCode"}
                    onChange={handleInputChange}
                    placeholder={"***"}
                  />
                </FormGroup>
              </div>
              <div className="col-12">
                <Button block color="secondary" type="button" disabled={ loading }
                        loading={ loading } onClick={ submitCredit }>Pagar</Button>
              </div>
            </div>
            <div>
              {/* credit / debit card */ }
              { isCardPayment ? (
                <div className="card-inputs">

                  <FormGroup>
                    <Label htmlFor="cardholder-name">Nombre impreso en tarjeta</Label>
                    <Input id="cardholder-name" name="cardHolderName" required={ requireds } type="text" onChange={ handleInputChange }/>
                  </FormGroup>

                  {/* mandatory data */ }

                  {/* card fields */ }
                  <FormGroup>
                    <div id="card-secure-fields"/>
                    <InvalidFeedback error={ error } loading={ loading } name="payment_os"/>
                  </FormGroup>


                  <Button block color="secondary" type="button" disabled={ loading }
                          loading={ loading } onClick={ submitCredit }>Pagar</Button>
                </div>

                // cash payment methods
              ) : values.payment_method_id === 3 && (
                <FormGroup>
                  { cashPaymentMethods == null ? (
                    <p>Cargando...</p>
                  ) : cashPaymentMethods.length ? cashPaymentMethods.map((item, key) => (
                    <InputRadio
                      key={ key }
                      label={ item.name }
                      name="cash_payment_method_id"
                      onChange={ onCashPaymentMethodChange }
                      state={ values.cash_payment_method_id }
                      value={ item.id }
                    />
                  )) : (
                    <p>Sin método de pago configurado.</p>
                  ) }
                  <InvalidFeedback error={ error } loading={ loading } name="cash_payment_method_id"/>
                  <Button block color="secondary" type="submit" disabled={ loading }
                          loading={ loading }>Pagar</Button>
                </FormGroup>
              ) }
            </div>
          </div>

          <div className="col-md-6">
            <div class="product-summary">
              <div className="product-image">
                <img src="https://place-hold.it/280x125" alt=""/>
              </div>
              <div className={ "product-name-group" }>
                <h6>
                  Você está comprando:
                </h6>
                <p class={ "product-name" }>
                  Assinatura LaU Play - <strong>{ selectedPackage.name }</strong> recorrente
                </p>
              </div>
              <div class={ "price-breakdown" }>
                <div className="checkout-total">
                  <h6>
                    Total
                  </h6>
                  <p class={ "price" }>
                    $ { selectedPackage.amount }
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

  )
}

export default withAuth(Payment, true);
