// import sleep from 'sleep-promise'
import FormGroup from '~/components/layout/AuthModal/FormGroup'
import Label from '~/components/Form/Label'
import Input from '~/components/layout/AuthModal/Input'
import React, { useContext, useEffect, useState } from 'react'
import withAuth from '~/components/withAuth'
import { IS_PRODUCTION } from "~/constants/constants";
import UserContext from "~/contexts/UserContext";
import Select from "~/components/Select/Select";
import Loading from "~/components/Loading/Loading";
import Button from "~/components/button";
import InvalidFeedback from "~/components/Form/InvalidFeedback";
import { GoSearch } from 'react-icons/go';
import Color from 'color'
import { ThemeContext } from 'styled-components'
import $ from 'jquery';
import ReactDOM from 'react-dom';
import Flatpickr from "react-flatpickr";
import { CONFIG } from "~/config";


const UserDataForm = ({ api, layoutProps, handleSubmit }) => {

  const theme = useContext(ThemeContext)
  const primaryColor
    = Color(theme.colors.primary).hsl().string()

  const requireds = IS_PRODUCTION

  const [loading, setLoading] = useState(false);
  const [date, setDate] = useState({

    birthdate: null,

  })

  const [values, setValues] = useState({
    name: '',
    gender_id: '',
    document: '',
    nickname: '',
    birthdate: null,
    registration: '',
    isPartner: ''
  })

  const { user } = useContext(UserContext)

  const [genders, setGenders] = useState()
  const [discounts, setDiscounts] = useState(false)
  const [blockDiscountFields, setBlockDiscountFields] = useState(false)

  const [error, setError] = useState()
  // const [errorCod, setErrorCod] = useState([])


  /* get genders */
  useEffect(_ => {
    (async _ => {
      const { data } = await api.get('genders')
      setGenders(data)
    })()
  }, [])

  /* fill user form */
  useEffect(_ => {
    if (user) {
      setDate({
        ...date,
        birthdate: new Date(),
      })
      setValues({
        ...values,
        name: user.name,
        gender_id: user.gender_id,
        document: user.document,
        nickname: user.nickname,
        birthdate: user.birthdate,
        registration: user.registration,
        isPartner: user.is_partner,
        abonado: user.abonado,
        terms: user.terms,
      })
    }

  }, [user])

  const handleInputChange = e => {
    const { checked, name, value, type } = e.target
    setValues({
      ...values,
      [name]: type === 'checkbox' ?
        (checked ? (value === 'true' ? true : value) : false) :
        value,
    })

  }

  const submit = async e => {
    e.preventDefault();

    setLoading(true);

    try {

      let userData = {
        name: values.name,
        gender_id: values.gender_id,
        document: values.document,
        email: user.email,
        nickname: values.nickname,
        registration: values.registration,
        birthdate: date.birthdate,
        is_partner: values.isPartner,
        abonado: values.abonado,
        terms: values.terms,

      }



      const res = await api.post(`register/complete-user`, userData)

      handleSubmit(1, userData)

    } catch (error) {
      if (error.response) {
