import MediaLink from '../MediaLink/MediaLink'
import PropTypes from 'prop-types'
import React, { useEffect } from 'react'
import Category from '../../types/Category'
import Media from '../../types/Media'
import WishlistBtn from "../../components/wishlist-btn"
import { useContext, useState, useRef } from 'react'
import { ThemeContext } from 'styled-components'
import Hls from "hls.js";

const MediaCard = ({
  category = null,
  className,
  media,
  wishlist
}) => {
  if ( ! media.thumbnail_url) {
    media.thumbnail_url = '//placehold.jp/180x256.png'
  }
  const theme = useContext(ThemeContext)
  const lightColor = theme.colors.texts
  const whiteColor = theme.colors.white
  const videoCustom = useRef()
  const { is_paid: isPaid } = media
  let [displayVideo, setDisplayVideo] = useState( false );

  useEffect(function() {
    (async () => {

      const hls = new Hls();
      const url = "https://bitdash-a.akamaihd.net/content/sintel/hls/playlist.m3u8";

      hls.loadSource(url);
      hls.attachMedia(videoCustom.current);
      hls.on(Hls.Events.MANIFEST_PARSED, function() { videoCustom.current.play(); })

    })();
  })
  const handleMouseEnter = function() {
    setTimeout(() => {
      setDisplayVideo(true)
      videoCustom.current.muted = true
      videoCustom.current.play();

    }, 800)
  }
  const handleMouseLeave = function() {
    setTimeout(() => {
      setDisplayVideo(false)
      videoCustom.current.muted = true
      videoCustom.current.pause();
    }, 500)
  }
  return (
    <div {...{className}}             onMouseEnter={handleMouseEnter}
         onMouseLeave={handleMouseLeave}
    >
      <MediaLink {...{category, media}}>
        <a className="media-card text-center">
          {wishlist &&
            <div className="wish">
              <WishlistBtn movieId={media.id} inside={true} />
            </div>}
          <img className="img-fluid" src={"https://via.placeholder.com/235x135"} />
          <div className="media-card-label">{media.title}</div>
          <video ref={videoCustom} muted={true} autoPlay={true} src="/static/file_example_MP4_480_1_5MG.mp4" style={{
            display: displayVideo ? 'block' : 'none',
          }}></video>
        </a>
      </MediaLink>
      <style jsx>{`
        video {
          margin: auto;
          position: absolute;
          top: 0;
          border: 0;
          right: 0;
          left: 0;
          bottom: 0;
          width: 100%;
          height: 100%;
        }
        .media-card {
          color: ${lightColor};
          display: block;
          font-size: 13px;
          line-height: 1;
          outline: 0;
          text-decoration: none;
          transition: color .2s;
        }
        .media-card:focus,
        .media-card:hover {
          color: ${whiteColor};
        }
        .media-card img {
          margin-bottom: 10px;
          transition: opacity .2s;
          will-change: opacity;
        }
        .media-card-label {
          padding-right: 5%;
          padding-left: 5%;
        }
        .media-card:focus img,
        .media-card:hover img {
          //opacity: .75;

        }
        .wish {
          position: absolute;
          z-index: 2;
          opacity: 0;
          text-align: left;
        }
        .media-card:focus .wish,
        .media-card:hover .wish {
          opacity: 1;
        }
        @media (min-width: 768px) {
          .media-card {
            font-size: 16px;
          }
        }
      `}</style>
    </div>
  )
}

MediaCard.defaultProps = {
  category: null,
  className: null,
  media: null,
}

export default MediaCard
