        }
        @media (min-width: 768px) {
          .home-carousel-section {
            //margin-bottom: 55px;
          }
        }
      ` }</style>
    </>
  )
}

const BannerSection = ({ id, movie }) => {
  const [banner, setBanner] = useState()
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState()

  useEffect(_ => {
    async function fetchData() {
      setLoading(true)
      try {
        const { data } = await (id ? api().get(`/banners/${ id }`) : api().get(`/movie/${ movie }`))
        setBanner(data)

      } catch (error) {
        setError(true)
      }
      setLoading(false)
    }

    fetchData()
  }, [id])

  return (
    <div>
      <div className="section">

        {/* loading */ }
        <div className="text-center">
          <Loading loadingState={ loading }/>
        </div>

        {/* features */ }
        { banner && id &&
        <a className="sponsor-link" href={ banner.link } target="_blank">
          <Featured img={ banner.banner_url }/>
        </a>
        }

        { banner && movie &&
        <MediaLink media={ banner.movie } passHref>
          <a>
            <Featured
              className="gradient"
              img={ banner.movie.poster_url }
            />
          </a>
        </MediaLink>
        }

        {/* error */ }
        { error && (
          <div className="text-center">Error</div>
        ) }

      </div>
      <style jsx>{ `
        .sponsor-link {
          color: white !important;
          text-decoration: none;
        }
        .sponsor-link:hover {
          color: white !important;
          text-decoration: none;
        }
        a:not([href]):not([tabindex]):hover {
          color: white !important;
          text-decoration: none;
        }
      ` }</style>
    </div>
  );
}
