// import { useContext } from 'react'
import Head from 'next/head'
import BlockedPlayer from '~/components/Player/BlockedPlayer'
// import CommentSection from '~/components/comment-section'
import Layout from '~/components/layout/Layout'
// import LikeNCommentsBtns from '~/components/like-n-comments-btns'
import MediaDescription from '~/components/media-description'
import MoreContentCarousel from '~/components/more-content-carousel'
// import SocialShareBtns from '~/components/social-share-btns'
// import UserContext from '~/contexts/UserContext'
import { CONFIG } from '~/config'
import { STATIC_PATH, TENANT } from '~/constants/constants'
import api from '~/services/api'
import MediaLink from '~/components/MediaLink/MediaLink'

export default function WatchPage({ errorCode, category, media, related, layoutProps }) {
  const { appName } = CONFIG
  const { title: mediaTitle } = media
  const pageTitle = `${mediaTitle} < ${appName}`
  return (
    <Layout errorCode={errorCode} {...layoutProps}>
      <Head>
        <title>{pageTitle}</title>
      </Head>
      <div className="container-fluid">
        <div className="row align-items-center">
          <div className="col-12 col-lg-8">
            <BlockedPlayer image={media.thumbnail2_url} media={media} />
          </div>
          { related && (
            <div className="col-12 col-lg-4 d-none d-md-block">
              <Related {...{category, medias: related}} />
            </div>
          )}
        </div>
        <div className="row">
          <div className="col-lg-8">
            <MediaDescription {...{media}} />
          </div>
        </div>
        {/* <div className="icons-row row">
          <div className="col">
            <LikeNCommentsBtns />
          </div>
          <div className="col text-right">
            <SocialShareBtns />
          </div>
        </div> */}
        { related && (
          <div className="d-md-none">
            <Related {...{category, medias: related}} />
          </div>
        )}
      </div>

      {category && (
        <MoreContentCarousel category={category} />
      )}

      {/* <CommentSection /> */}

      <style jsx>{`
        .row:first-child {
          margin-bottom: 15px;
        }
        .icons-row {
          margin-bottom: 25px;
        }
        @media (min-width: 1200px) {
          .container-fluid {
            padding-right: 4%;
            padding-left: 4%;
          }
          .row:first-child {
            margin-bottom: 30px;
          }
        }
      `}</style>
    </Layout>
  );
}

WatchPage.getInitialProps = async (ctx) => {
  const {slug, category: categorySlug} = ctx.query;
  try {
    const response = await api(ctx).get(`/movie/${slug}/category/${categorySlug}`)
    const { category, movie, related } = response.data
    return { category, media: movie, related }
  } catch (error) {
    const errorCode = 404
    return { errorCode }
  }
}

function Related({category, medias}) {
  return (
    <div>
      { medias.map((media, key) => (
        <RelatedVideo {...{category, media, key}} />
      )) }
    </div>
  )
}

function RelatedVideo({category = null, media}) {
  return (
    <div className="more-card">
      <MediaLink {...{category, media}} watch>
        <a>
          <div className="row align-items-center gutter-15">
            <div className="col-7">
              <img
                className="img-fluid"
                height="123"
                src={media.thumbnail2_url ? media.thumbnail2_url : '//placehold.jp/220x123.png'} width="220"
              />
            </div>
            <div className="col-4">
              <p><strong>{media.title}</strong></p>
            </div>
          </div>
        </a>
      </MediaLink>
      <style jsx>{`
        .more-card {
          font-size: 11px;
          line-height: 1.5;
          margin-bottom: 15px;
        }
        .more-card a {
          display: block;
          text-decoration: none;
        }
        .more-card a:focus,
        .more-card a:hover {
          color: var(--white);
        }
        .more-card p {
          margin-bottom: 0;
        }
        @media (min-width: 768px) {
          .more-card {
            line-height: 1.75;
            margin-bottom: 0;
            padding-top: 15px;
            padding-bottom: 15px;
          }
        }
      `}</style>
    </div>
  )
}
