import { useEffect, useState } from "react";
import FormGroup from "~/components/layout/AuthModal/FormGroup";
import Label from "~/components/Form/Label";
import Input from "~/components/layout/AuthModal/Input";
import InputRadio from "~/components/Form/InputRadio";
import withAuth from "~/components/withAuth";
import InvalidFeedback from "~/components/Form/InvalidFeedback"
import { HAS_WINDOW } from "~/constants/constants";
import useScript from "@charlietango/use-script";
import Select from "~/components/Select/Select";
import Button from "~/components/button";

const Payment = ({
                   layoutProps,
                   package_id,
                   packages,
                   api,
                   error,
                   requireds
                 }) => {

  const [ready, status] = useScript('https://secure.mlstatic.com/sdk/javascript/v1/mercadopago.js')


  let selectedPackage = null
  if (packages && package_id) {
    selectedPackage = packages.items.find(i => i.id == package_id)
    console.log(selectedPackage)
  }


  const [values, setValues] = useState({
    package_id: '',
    payment_method_id: null,
    payment_os: null,
    cash_payment_method_id: null,
    terms: null,
    discount_id: null,
  })

  const [loading, setLoading] = useState();

  const payUEnv = 'test'
  const businessUnitPublicKey = '88985036-6530-4b5a-a7ec-c4e07ec07f6c'
  const [isPayUReady, setIsPayUReady] = useState(false)
  const POS = ready && HAS_WINDOW ? window.MercadoPago : null
  const credit_card_id = 1
  const debit_card_id = 2
  const isCardPayment = [credit_card_id, debit_card_id].includes(values.payment_method_id)


  const POSStyle = {
    base: {
      borderRadius: '.15rem',
      backgroundColor: 'white',
      height: 'calc(1.5em + .75rem + 2px)',
      fontSize: '12px',
      padding: '0px 3px',
      marginLeft: '5px',
      cardImage: {
        borderTopRightRadius: 0,
        borderBottomRightRadius: 0,
        margin: 0,
      },
      pan: {
        width: '160px',
        borderTopLeftRadius: 0,
        borderBottomLeftRadius: 0,
        marginLeft: 0,
      },
      expirationDate: { width: '47px' },
      secureFields: { left: '40px' },
      cvv: { width: '35px' }
    }
  }


  useEffect(_ => {
    if (POS) {
      POS.setPublicKey(businessUnitPublicKey)
      POS.setEnvironment(payUEnv)
      POS.setStyle(POSStyle)
      POS.setCardNumberPlaceholder('Tarjeta de crÃ©dito')
      POS.setExpirationDatePlaceholder('MM/AA');
      setIsPayUReady(true)
    }
  }, [POS])


  /* handle payment method change */
  function onPaymentChange(e) {
    setValues({
      ...values,
      payment_method_id: parseInt(e.target.value, 10),
      cash_payment_method_id: null,
    })
  }

  function onCashPaymentMethodChange(e) {
    setValues({
      ...values,
      cash_payment_method_id: parseInt(e.target.value, 10),
    })
  }

  const handleInputChange = e => {
    const { checked, name, value, type } = e.target
    setValues({
      ...values,
      [name]: type === 'checkbox' ?
        (checked ? (value === 'true' ? true : value) : false) :
        value,
    })
  }

  // payment methods
  const [paymentMethods, setPaymentMethods] = useState()

  // get payment methods
  useEffect(_ => {
    const getPaymentMethods = async _ => {
      const { data } = await api.get('payment-methods')
      setPaymentMethods(data)
    }
    getPaymentMethods()
  }, [])

  // init card secure fields
  useEffect(_ => {
    if (isPayUReady && isCardPayment) {
      POS.initSecureFields('card-secure-fields')
    }
  }, [isPayUReady, isCardPayment])

  // cash payment methods
  const [cashPaymentMethods, setCashPaymentMethods] = useState()

  // get cash payment methods
  useEffect(_ => {
    const getCashPaymentMethods = async _ => {
      const { data } = await api.get('cash-payment-methods')
      setCashPaymentMethods(data)
    }
    getCashPaymentMethods()
  }, [])


  /* token */
  async function createToken() {
    const promise = new Promise((resolve, reject) => {
      const additionalData = {
        holder_name: document.getElementById('cardholder-name').value,
        // custom_data: document.getElementById('custom').value,
      }
      POS.createToken(additionalData, (result) => {
        const json = JSON.parse(result)
        console.log('json', json)
        json.token ? resolve(json) : reject(json)
      })
    })
    return promise
  }


  const submitCredit = async e => {
    e.preventDefault();

    setLoading(true);

    const paymentData = values.package_id && values.package_id !== free_package_id &&
    values.payment_method_id && isCardPayment ? await createToken() : null
    const data = { ...values, payment_os: paymentData }

    try {

      const res = await api.post(`register/subscribe`, {
        package_id: package_id,
        payment_method_id: values.payment_method_id,
      })

      // handleSubmit(1)

    } catch (error) {

      if (error.response) {
        const { data, status } = error.response
        if (status === 422) {
          setError(data)
        }
      } else if (error.request) {
        setError(error)
      } else {
        setError(error)
      }

    } finally {
      setLoading(false)
    }


  }

  return (
    <div className="row">
      <div className="offset-md-2 col-md-8">

        <h3 className="h3">Pago</h3>

        <div className="row">

          <div className="col-md-6 paymentMethod">
            <div>
              <FormGroup>
                <Label htmlFor="payment_method_id" style="margin: 0;">Pague com</Label>
                <Select
                  id="payment_method_id"
                  name="payment_method_id"
                  required={ requireds }
                  onChange={ onPaymentChange }
                  value={ values.payment_method_id }
                >
                  <option value="0">Selecione</option>
                  { paymentMethods && paymentMethods.map((paymentMethod, key) => (
                    <option
                      key={ paymentMethod.id }
                      state={ values.payment_method_id }
                      value={ paymentMethod.id }
                    >
                      { paymentMethod.name }
                    </option>
                  )) }
                </Select>
                <InvalidFeedback error={ error } loading={ loading } name="gender_id"/>
              </FormGroup>
            </div>
            <div>
              {/* credit / debit card */ }
              { isCardPayment ? (
                <div className="card-inputs">

                  {/* mandatory data */ }
                  <FormGroup>
                    <Label htmlFor="cardholder-name">Nombre impreso en tarjeta</Label>
                    <Input id="cardholder-name" name="cardholder-name" required={ requireds } type="text"/>
                  </FormGroup>

                  {/* card fields */ }
                  <FormGroup>
                    <div id="card-secure-fields"/>
                    <InvalidFeedback error={ error } loading={ loading } name="payment_os"/>
                  </FormGroup>


                  <Button block color="secondary" type="button" disabled={ loading }
                          loading={ loading } onClick={ submitCredit }>Pagar</Button>
                </div>

                // cash payment methods
              ) : values.payment_method_id === 3 && (
                <FormGroup>
                  { cashPaymentMethods == null ? (
                    <p>Cargando...</p>
                  ) : cashPaymentMethods.length ? cashPaymentMethods.map((item, key) => (
                    <InputRadio
                      key={ key }
                      label={ item.name }
                      name="cash_payment_method_id"
                      onChange={ onCashPaymentMethodChange }
                      state={ values.cash_payment_method_id }
                      value={ item.id }
                    />
                  )) : (
                    <p>Sin mÃ©todo de pago configurado.</p>
                  ) }
                  <InvalidFeedback error={ error } loading={ loading } name="cash_payment_method_id"/>
                  <Button block color="secondary" type="submit" disabled={ loading }
                          loading={ loading }>Pagar</Button>
                </FormGroup>
              ) }
            </div>
          </div>

          <div className="col-md-6">
            <div class="product-summary">
              <div className="product-image">
                <img src="https://place-hold.it/280x125" alt=""/>
              </div>
              <div className={ "product-name-group" }>
                <h6>
                  VocÃª estÃ¡ comprando:
                </h6>
                <p class={ "product-name" }>
                  Assinatura LaU Play - <strong>{ selectedPackage.name }</strong> recorrente
                </p>
              </div>
              <div class={ "price-breakdown" }>
                <div className="checkout-total">
                  <h6>
                    Total
                  </h6>
                  <p class={ "price" }>
                    $ { selectedPackage.amount }
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

  )
}

export default withAuth(Payment, true);
